import{_ as e}from"./chunks/transfer-ownership.BLcX9q3F.js";import{_ as r,o as a,c as o,ag as n}from"./chunks/framework.BnOSF0Yy.js";const m=JSON.parse('{"title":"Ownership Transfer","description":"","frontmatter":{},"headers":[],"relativePath":"en/guide/ownership-transfer.md","filePath":"en/guide/ownership-transfer.md"}'),i={name:"en/guide/ownership-transfer.md"};function s(l,t,d,h,p,f){return a(),o("div",null,[...t[0]||(t[0]=[n('<h1 id="ownership-transfer" tabindex="-1">Ownership Transfer <a class="header-anchor" href="#ownership-transfer" aria-label="Permalink to &quot;Ownership Transfer&quot;">​</a></h1><p>When an open-source project needs to be moved from a personal account to an organization, or transferred between users, GitHub provides the Transfer ownership feature.</p><h2 id="when-to-transfer" tabindex="-1">When to Transfer <a class="header-anchor" href="#when-to-transfer" aria-label="Permalink to &quot;When to Transfer&quot;">​</a></h2><ul><li><strong>Personal project to organization</strong>: The project has grown and needs team-based management</li><li><strong>Departure/exit</strong>: The original maintainer is no longer involved and needs to hand off to a new maintainer</li><li><strong>Community incubation</strong>: A personally initiated project is adopted by a community and moved under a community organization</li></ul><h2 id="preparation-before-transfer" tabindex="-1">Preparation Before Transfer <a class="header-anchor" href="#preparation-before-transfer" aria-label="Permalink to &quot;Preparation Before Transfer&quot;">​</a></h2><p>Before transferring, confirm the following:</p><ol><li><strong>Notify all collaborators</strong>: Inform the project&#39;s collaborators and active contributors in advance</li><li><strong>Back up important data</strong>: Although GitHub transfer preserves most data, a local backup is recommended</li><li><strong>Check CI/CD configuration</strong>: The repository URL will change after transfer — related configurations need updating</li><li><strong>Confirm target account/organization permissions</strong>: You must be an Owner of the target organization</li></ol><h2 id="transfer-steps" tabindex="-1">Transfer Steps <a class="header-anchor" href="#transfer-steps" aria-label="Permalink to &quot;Transfer Steps&quot;">​</a></h2><ol><li>Go to the repository&#39;s <strong>Settings</strong> page</li><li>Scroll to the bottom to the <strong>Danger Zone</strong></li><li>Click <strong>Transfer ownership</strong></li><li>Enter the target account or organization name</li><li>Type the repository name to confirm</li><li>Click to confirm the transfer</li></ol><p><img src="'+e+'" alt="alt text"></p><h2 id="what-happens-after-transfer" tabindex="-1">What Happens After Transfer <a class="header-anchor" href="#what-happens-after-transfer" aria-label="Permalink to &quot;What Happens After Transfer&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Item</th><th>Preserved?</th></tr></thead><tbody><tr><td>Code and commit history</td><td>Yes</td></tr><tr><td>Issues and PRs</td><td>Yes</td></tr><tr><td>Stars and Watchers</td><td>Yes</td></tr><tr><td>Fork relationships</td><td>Yes</td></tr><tr><td>GitHub Pages</td><td>Needs reconfiguration</td></tr><tr><td>Webhooks</td><td>Preserved but URLs may need updating</td></tr><tr><td>Old URL redirects</td><td>GitHub redirects automatically</td></tr></tbody></table><div class="warning custom-block"><p class="custom-block-title">Note</p><p>Although GitHub automatically redirects the old URL to the new one, it is recommended to update the following as soon as possible after transfer:</p><ul><li>Repository links in project documentation</li><li>Repository URLs in CI/CD configurations</li><li>Repository information in package managers such as npm / PyPI</li></ul></div><h2 id="next-step" tabindex="-1">Next Step <a class="header-anchor" href="#next-step" aria-label="Permalink to &quot;Next Step&quot;">​</a></h2><p>Now that you understand ownership transfer, learn how to preview your documentation site from a fork — <a href="./fork-preview.html">Fork Site Preview</a>.</p>',15)])])}const g=r(i,[["render",s]]);export{m as __pageData,g as default};
