import{_ as r}from"./chunks/transfer-ownership.BLcX9q3F.js";import{_ as a,o as e,c as i,ag as o}from"./chunks/framework.BnOSF0Yy.js";const b=JSON.parse('{"title":"所有权转移","description":"","frontmatter":{},"headers":[],"relativePath":"guide/ownership-transfer.md","filePath":"zh/guide/ownership-transfer.md"}'),l={name:"guide/ownership-transfer.md"};function s(n,t,d,h,c,g){return e(),i("div",null,[...t[0]||(t[0]=[o('<h1 id="所有权转移" tabindex="-1">所有权转移 <a class="header-anchor" href="#所有权转移" aria-label="Permalink to &quot;所有权转移&quot;">​</a></h1><p>当一个开源项目需要从个人账号迁移到组织账号，或者在不同用户之间转移时，GitHub 提供了 Transfer ownership 功能。</p><h2 id="什么场景需要转移" tabindex="-1">什么场景需要转移 <a class="header-anchor" href="#什么场景需要转移" aria-label="Permalink to &quot;什么场景需要转移&quot;">​</a></h2><ul><li><strong>个人项目 → 组织</strong>：项目发展壮大，需要团队协作管理</li><li><strong>离职/退出</strong>：原维护者不再参与，需要交接给新的维护者</li><li><strong>社区孵化</strong>：个人发起的项目被社区采纳，转移到社区组织下</li></ul><h2 id="转移前的准备" tabindex="-1">转移前的准备 <a class="header-anchor" href="#转移前的准备" aria-label="Permalink to &quot;转移前的准备&quot;">​</a></h2><p>在转移之前，确认以下事项：</p><ol><li><strong>通知所有协作者</strong>：提前告知项目的 collaborator 和活跃贡献者</li><li><strong>备份重要数据</strong>：虽然 GitHub 转移会保留大部分数据，但建议本地备份</li><li><strong>检查 CI/CD 配置</strong>：转移后仓库 URL 会变化，需要更新相关配置</li><li><strong>确认目标账号/组织的权限</strong>：你需要是目标组织的 Owner</li></ol><h2 id="转移步骤" tabindex="-1">转移步骤 <a class="header-anchor" href="#转移步骤" aria-label="Permalink to &quot;转移步骤&quot;">​</a></h2><ol><li>进入仓库的 <strong>Settings</strong> 页面</li><li>滚动到最底部的 <strong>Danger Zone</strong></li><li>点击 <strong>Transfer ownership</strong></li><li>输入目标账号或组织名称</li><li>输入仓库名称确认</li><li>点击确认转移</li></ol><p><img src="'+r+'" alt="alt text"></p><h2 id="转移后会发生什么" tabindex="-1">转移后会发生什么 <a class="header-anchor" href="#转移后会发生什么" aria-label="Permalink to &quot;转移后会发生什么&quot;">​</a></h2><table tabindex="0"><thead><tr><th>项目</th><th>是否保留</th></tr></thead><tbody><tr><td>代码和提交历史</td><td>保留</td></tr><tr><td>Issues 和 PR</td><td>保留</td></tr><tr><td>Stars 和 Watchers</td><td>保留</td></tr><tr><td>Fork 关系</td><td>保留</td></tr><tr><td>GitHub Pages</td><td>需要重新配置</td></tr><tr><td>Webhooks</td><td>保留但可能需要更新 URL</td></tr><tr><td>旧 URL 重定向</td><td>GitHub 自动重定向</td></tr></tbody></table><div class="warning custom-block"><p class="custom-block-title">注意</p><p>虽然 GitHub 会自动将旧 URL 重定向到新 URL，但建议转移后尽快更新以下内容：</p><ul><li>项目文档中的仓库链接</li><li>CI/CD 配置中的仓库地址</li><li>npm / PyPI 等包管理器中的仓库信息</li></ul></div>',13)])])}const _=a(l,[["render",s]]);export{b as __pageData,_ as default};
